DROP TABLE IF EXISTS `post`;
CREATE TABLE IF NOT EXISTS `post` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `cid` smallint(6) NOT NULL DEFAULT '0' COMMENT '咨询类别',
